import React from 'react';

class Question extends React.Component {
  constructor(props) {
  super(props)
  this.state = {
      text: props.text,
      answer: props.answer
    }
  }
  checkAnswer = (event) => {
    console.log(event);
  }
  render(){
    return (
      <div>
        <p>{this.props.text}</p>
        <button onClick={this.checkAnswer()} type="button" name="button" value="true">True</button>
        <button onClick={this.checkAnswer()} type="button" name="button" value="false">False</button>
      </div>
    )
  }
}
export default Question;
