import React from 'react';

class Question extends React.Component {



  checkAnswerTrue = () => {
    console.log(this.props.answer)
    // debugger;
    if (this.props.answer === true) {
      alert("You're right!")
    } else {
      alert("You're wrong.")
    }
  }

  checkAnswerFalse = () => {
    if (this.props.answer === false) {
      alert("You're right!")
    } else {
      alert("You're wrong.")
    }
  }

  render() {
    // debugger;
    return (
      <li answer={this.props.answer.toString()}>
        {this.props.text}
        <br/>
        <button onClick={this.checkAnswerTrue}>True</button><button onClick={this.checkAnswerFalse}>False</button>
      </li>
    );
  }
}

export default Question;
